INSERT INTO `posts` (`id`, `forum_id`, `title`, `cached_slug`, `body`, `wiki_body`, `truncated_body`, `created_at`, `updated_at`) VALUES (3,1,'Cherche disque dur SSD capacité < 250Go','cherche-disque-dur-ssd-capacite-250go','<p>Bonjour ami linuxiens, je cherche un disque dur SSD car j\'espère trouver quelque chose de léger rapide et résistant aux chocs. Je voudrai l\'utiliser en USB avec un fs en ext4 normalement, une partition /home et une partition racine.</p>\n\n<p>Avez vous quelque chose à me conseiller ?</p>\n\n<p>Cordialement,</p>\n\n<p>David</p>','\r\nBonjour ami linuxiens, je cherche un disque dur SSD car j\'espère trouver quelque chose de léger rapide et résistant aux chocs. Je voudrai l\'utiliser en USB avec un fs en ext4 normalement, une partition /home et une partition racine.\r\n\r\nAvez vous quelque chose à me conseiller ?\r\n\r\nCordialement,\r\n\r\nDavid\r\n','<p>Bonjour ami linuxiens, je cherche un disque dur SSD car j\'espère trouver quelque chose de léger rapide et résistant aux chocs. Je voudrai l\'utiliser en USB avec un fs en ext4 normalement, une partition /home et une partition racine.</p>\n\n<p>Avez vous quelque chose à me conseiller ?</p>\n\n<p>Cordialement,</p>\n\n<p>David</p>','2012-11-19 05:38:13','2012-11-19 05:38:13');
INSERT INTO `posts` (`id`, `forum_id`, `title`, `cached_slug`, `body`, `wiki_body`, `truncated_body`, `created_at`, `updated_at`) VALUES (4,2,'Donne tour PC Gateway 700 en état de marche','donne-tour-pc-gateway-700-en-etat-de-marche','<p>Bonjour,<br />\nHistoire qu\'elle ne finisse pas éventrée sur le trottoir le jour des encombrants, je donne une tour de PC Gateway 700 en état de marche. Sa description sommaire:<br />\nPentium PIII 700 Mhz<br />\nCarte Mère et carte son d\'origine, le reste a été upgradé :<br />\nRam 768 Mo<br />\nCarte vidéo Radeon 7500 (remplace la carte originale)<br />\n2 disques durs IDE (40+20 Go)<br />\n1 lecteur DVD<br />\n1 graveur CD 52x32x52<br />\n1 carte PCI USB 2 (4 ports en plus des 2 ports USB de la CM)<br />\n1 carte ethernet 10/100 (Realtek je crois)<br />\nSans lecteur de disquette, sans périphérique, ni système d\'exploitation.</p>\n\n<p>Elle tournait sur Debian il y a encore 2 ans. Tout est le matériel est reconnu.<br />\nC\'est à venir prendre à Courbevoie (92400).</p>','\r\n\r\nBonjour,\r\nHistoire qu\'elle ne finisse pas éventrée sur le trottoir le jour des encombrants, je donne une tour de PC Gateway 700 en état de marche. Sa description sommaire:\r\nPentium PIII 700 Mhz\r\nCarte Mère et carte son d\'origine, le reste a été upgradé :\r\nRam 768 Mo\r\nCarte vidéo Radeon 7500 (remplace la carte originale)\r\n2 disques durs IDE (40+20 Go)\r\n1 lecteur DVD\r\n1 graveur CD 52x32x52\r\n1 carte PCI USB 2 (4 ports en plus des 2 ports USB de la CM)\r\n1 carte ethernet 10/100 (Realtek je crois)\r\nSans lecteur de disquette, sans périphérique, ni système d\'exploitation.\r\n\r\nElle tournait sur Debian il y a encore 2 ans. Tout est le matériel est reconnu.\r\nC\'est à venir prendre à Courbevoie (92400).\r\n','<p>Bonjour,<br>\nHistoire qu\'elle ne finisse pas éventrée sur le trottoir le jour des encombrants, je donne une tour de PC Gateway 700 en état de marche. Sa description sommaire:<br>\nPentium PIII 700 Mhz<br>\nCarte Mère et cart